// Hardcoded JWT Token
const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6Im5vZGVyZWQiLCJyb2xlIjoic3lzdGVtIiwiaWF0IjoxNzY2MzY4ODk2LCJleHAiOjE3NjY0NTUyOTZ9.7kzVowdCJmpGvmFVZPwBq7hs6D2YHlRDILUpbQpYHtU";

// Random status
const statuses = ["running", "idle", "warning", "error"];
// Or only running:
// const statuses = ["running"];

function randomStatus() {
    return statuses[Math.floor(Math.random() * statuses.length)];
}

// Random number generators
function randomInt(min, max) { 
    return Math.floor(Math.random() * (max - min + 1)) + min; 
}

function randomFloat(min, max) { 
    return parseFloat((Math.random() * (max - min) + min).toFixed(2)); 
}

// Generate random machine data for D-01
msg.payload = {
    status: randomStatus(),
    lineSpeed: randomInt(800, 1000),
    current: randomFloat(40, 50),
    power: randomFloat(60, 80),
    temperature: randomFloat(65, 75),
    healthScore: randomInt(85, 100),
    vibrationLevel: "Normal",
    runtimeHours: randomFloat(150, 200)
};

// Set headers with JWT token
msg.headers = {
    "Authorization": "Bearer " + token,
    "Content-Type": "application/json"
};

// Set HTTP method and URL
// Note: API uses machine NAME "Drawing Line 01" (not ID "D-01")
msg.method = "PUT";
msg.url = "http://localhost:3001/api/machines/name/Drawing Line 01";

return msg;
